% grid style \pgfplotsset{grid style={dotted,gray!90}}
% for annotating equations \newcommand{\tikzmark}[1]{\tikz[overlay,remember picture] \node[baseline] (#1) {};}% \NewDocumentCommand{\Annotate}{O{} O{} m m m m m m}{% % #1 = line draw options % #2 = node options % #3 = x-coordinate displacement start % #4 = y-coordinate displacement start % #5 = x-coordinate displacement end % #6 = y-coordinate displacement end % #7 = tikzmark name % #8 = node text % Example: % X =\tikzmark{Point A} Y % \Annotate[][anchor=south west]{-1.5ex}{1.5ex}{0.7}{0.5}{Point A}{Always true} \begin{tikzpicture}[remember picture, overlay] \draw [thick, -latex, shorten >=1pt, #1] (#7) ++ (#3,#4) -- +(#5,#6) node [black, right, draw=black, #2] {#8}; \end{tikzpicture}% }%

docinfo.macros:
% These macros are automatically generated from the "macros"
% XML element.  Make permanent edits there.
%
%%%%%%%%%%%%%%%%%%%%%
%
%     Conveniences
%
%%%%%%%%%%%%%%%%%%%%%
%
%  Integers
%  Usage:  \Z
\newcommand{\Z}{\mathbb{Z}}
%
%  Real numbers, as set of scalars
%  Usage:  \reals
\newcommand{\reals}{\mathbb{R}}
%
%  n-space over real field
%  Usage: \complex{integer-dimension}
\newcommand{\real}[1]{\mathbb{R}^{#1}}
%
%  evaluate a function
%  Usage: \fe{function-name}{input}
\newcommand{\fe}[2]{\mathop{{#1}{\left(#2\right)}}}
%
%  closed interval
%  Usage: \cinterval{left-endpoint}{right-endpoint}
\newcommand{\cinterval}[2]{\left[#1,#2\right]}
%
%  open interval
%  Usage: \ointerval{left-endpoint}{right-endpoint}
\newcommand{\ointerval}[2]{\left(#1,#2\right)}
%
%  closed-open interval
%  Usage: \cointerval{left-endpoint}{right-endpoint}
\newcommand{\cointerval}[2]{\left[\left.#1,#2\right)\right.}
%
%  open-closed interval
%  Usage: \ocinterval{left-endpoint}{right-endpoint}
\newcommand{\ocinterval}[2]{\left(\left.#1,#2\right]\right.}
%
%  point
%  Usage: \point{x}{y}
\newcommand{\point}[2]{\left(#1,#2\right)}
%
%  first derivative
%  Usage: \fd{f}
\newcommand{\fd}[1]{#1'}
%
%  second derivative
%  Usage: \sd{f}
\newcommand{\sd}[1]{#1''}
%
%  third derivative
%  Usage: \td{f}
\newcommand{\td}[1]{#1'''}
%
%  Leibniz notation
%  Usage: \lz{y}{x}
\newcommand{\lz}[2]{\frac{d#1}{d#2}}
%
%  higher Leibniz notation
%  Usage: \lzn{n}{y}{x}
\newcommand{\lzn}[3]{\frac{d^{#1}#2}{d#3^{#1}}}
%
%  Leibniz operator
%  Usage: \lzo{x}
\newcommand{\lzo}[1]{\frac{d}{d#1}}
%
%  Leibniz operator on ....
%  Usage: \lzoo{x}{y}
\newcommand{\lzoo}[2]{{\frac{d}{d#1}}{\left(#2\right)}}
%
%  higher Leibniz operator
%  Usage: \lzon{x}{y}{n}
\newcommand{\lzon}[2]{\frac{d^{#1}}{d#2^{#1}}}
%
%  Leibniz operator at ....
%  Usage: \lzoa{y}{x}{a}
\newcommand{\lzoa}[3]{\left.{\frac{d#1}{d#2}}\right|_{#3}}
%
%  Absolute Value
%  Usage: \abs{x}
\newcommand{\abs}[1]{\left|#1\right|}
%
