\begin{tikzpicture}
\begin{axis}[xlabel = {$t$},]
    \addplot[pccplot,
             domain=-6.7:-4,
             <-,]
             {1+6/5*(x+4)};
    \addplot[pccplot,
             domain=-4:-2,
             -]
             {4};
             ]
    \addplot[pccplot,
             domain=-2:3,
             -,]
             {-x+2};
             ]
    \addplot[pccplot,
             domain=0:22.4536998,
             variable=t,
             <-,]
             ({5-2/8.5*1.1^t},{1/(1/8.5*1.1^t)-2});
    \addplot[pccplot,
             domain=0:15.38769119,
             variable=t,
             ]
             ({5+1/sqrt(6.5)*1.1^t},{1/(1/sqrt(6.5)*1.1^t)^2});
    \addplot[asymptote,<-
    ]coordinates{
        (5,7)
        (5,-7)} node[above left] {\rotatebox{90}{$t=5$}};
    \addplot[soldot
    ]coordinates{
        (-4,4)
        (2,-3)};
    \addplot[holdot
    ]coordinates{
        (-4,1)
        (-1,3)
        (2,0)};
\end{axis}
\end{tikzpicture}
