\begin{tikzpicture}
\begin{axis}[]
    \addplot[pccplot,
        domain=-6.7:-5,
        <-,
    ]{3};
    \addplot[pccplot,
        domain=-5:-1,
        -,
    ]{(x+3)^2-1};
    \addplot[pccplot,
        domain=-1:3,
        -,
    ]{3-cos(90*x)};
    \addplot[pccplot,
        domain=3:4,
        -,
    ]{-2*(x-3)-2};
    \addplot[pccplot,
        domain=4:6.7,
        ->,
    ]{-2/3*(x-4)+6};
    \addplot[
        soldot
    ]coordinates{
        (3,3)
        (4,6)};
    \addplot[
        holdot
    ]coordinates{
        (3,-2)
        (4,-4)};
\end{axis}
\end{tikzpicture}
