\begin{tikzpicture}
\begin{axis}[xlabel = {$t$},]
    \addplot[
        pccplot,
        ->,
        ]coordinates{
            (-2,5)
            (-6.9,-2.35)};
    \addplot[
        pccplot,
        -,
        ]coordinates{
            (-2,-3.5)
            (3,-1)};
    \addplot[
        pccplot,
        ->,
        domain=3:6.9,
        ]{(x-4)^2-2};
    \addplot[
        soldot,
        ]coordinates{
            (-2,5)
            (5,1)};
    \addplot[
        holdot,
        ]coordinates{
            (-2,-3.5)
            (2,-1.5)
            (5,-1)};
\end{axis}
\end{tikzpicture}
