\begin{tikzpicture}
\begin{axis}[]
    \addplot[pccplot,
        domain=-6.8:-3,
        <-,
    ]{-2*(abs(x+4))^(1/3)*(x+4)/abs(x+4)+1};
    \addplot[pccplot,
        domain=-3:-1,
        -,
    ]{2*x+5};
    \addplot[pccplot,
        domain=-1:4,
        -,
    ]{2*x-3};
    \addplot[pccplot,
        domain=4:6.8,
        ->,
    ]{-4*(x-4)+5};
    \addplot[
        soldot
    ]coordinates{
        (-1,3)};
    \addplot[
        holdot
    ]coordinates{
        (-1,-5)};
\end{axis}
\end{tikzpicture}
