\begin{tikzpicture}
\begin{axis}[]
    \addplot[pccplot,
             variable=\t,
             domain=0:28.8167615,
             ]
             ({2+1/sqrt(13)*1.1^t}, {-6.5+1/(1/sqrt(13)*1.1^t)^2}); 
    \addplot[pccplot,
             variable=\t,
             domain=0:35.489593,
             ]
             ({2-1/sqrt(13)*1.1^t}, {-6.5+1/(1/sqrt(13)*1.1^t)^2});
    \addplot[asymptote
    ]coordinates{
        (2,-7)
        (2,7)} node[below right] {\rotatebox{90}{$x=2$}};
\end{axis}
\end{tikzpicture}

