\begin{tikzpicture}
\begin{axis}[xmin=-2,
             xmax=11,
             ymin=-2,
             ymax=11,
             xtick = {-2,0,...,10},
             ytick = {-2,0,...,10},
             minor xtick = {-2,-1,...,11},
             minor ytick = {-2,-1,...,11},
             legend entries = {$y=\fe{f}{x}$,{tangent line}},]
    \addplot+[variable=t,
        domain=0:3.25,
        ->,
    ]({t^2},{t});
    \addplot+[
        domain=-1.8:10.8,
        <->,
    ]{1+x/4};
    \addplot[
        soldot
    ]coordinates{
        (0,0)
        (4,2)};
\end{axis}
\end{tikzpicture}

\begin{tikzpicture}
\begin{axis}[xmin=-7,
             xmax=7,
             ymin=-2,
             ymax=12,
             xtick = {-6,-4,...,6},
             ytick = {-2,0,...,10},
             minor xtick = {-7,-6,...,7},
             minor ytick = {-2,-1,...,12},
             legend entries = {$y=\fe{g}{x}$,{tangent line}}]
    \addplot+[variable=t,
        domain=5:1.8,
        ->,
    ]({4-(5-t)^2},{t});
    \addplot+[
        domain=-6.8:6.8,
        <->,
    ]{2.5+x/2};
    \addplot[
        soldot
    ]coordinates{
        (4,5)
        (3,4)};
\end{axis}
\end{tikzpicture}
